mapper.update:
UPDATE user_board SET
board_title = #{board_Title}, board_price = #{board_Price}, board_text = #{board_Text}, board_img = #{board_Img},
loc_code = #{loc_code}, detail_loc = #{detail_loc}, user_code = #{user_code}, user_nickname = #{user_nickname} WHERE board_id = #{board_Id}

UPDATE user_board
SET board_click = board_click + 1
WHERE board_id = #{boardId}

UPDATE user_board
SET board_date = sysdate
WHERE board_id = #{boardId}

mapper.select:
SELECT COUNT(*) AS likeCount
FROM board_like
WHERE board_id = #{boardId}
